// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

//import "../node_modules/@openzeppelin/contracts/access/Ownable.sol";

contract Dswitch {

    address payable presetAddress;
    bool active;

    event StillActive(
        string status
    );

    event Transfer(
        address _from,
        address _to,
        uint amount
    );

     constructor(address payable pAddress) public {
        presetAddress = pAddress;
        active = true;
    }

    function still_active() public view returns(bool) {
        //active = true;
        //emit StillActive("User Still Alive");
        return active;
    }

    function transfer() public payable {
        active = false;
        presetAddress.transfer(msg.sender.balance);
        emit Transfer(msg.sender, presetAddress, msg.sender.balance);
    }

}
